        function DeleteRole(Id) {
            $.ajax({
                type: ""POST"",
                url: ""/Admin/Roles/DeleteRole"",
                data: {Id:Id},
                dataType: ""json"",
                success: function (response) {
                    location.reload();
                }
            });
        }


        function ChangeStatus(Id) {
            if (Id) {
                $.ajax({
                    type: ""POST"",
                    url: ""/Admin/Roles/ChangeStatus"",
                    data: {Id:Id},
                    dataType: ""json"",
                    success: function (res) {
                        if (res.isActive === true) {
